import { useState, useEffect } from 'react';
import './sty'


function App() {
  const [use, setUse] = useState([]);

  useEffect(() => {

    const loadApi = () =>{
      let url = 'https://jsonplaceholder.typicode.com/users';

      fetch(url)
      .then((result) => result.json())
      .then((json) => {
        console.log(json);
        setUse(json);
      })
    }

    loadApi();

  },[])


  return (
    <div className='container'>
      <header>
        <strong>Lista de usuaris</strong>
      </header>
    </div>
  );
}

export default App;
